SET NAMES UTF8;
DROP DATABASE IF EXISTS vg;
CREATE DATABASE vg CHARSET=UTF8;
USE vg;

-- vg_user
CREATE TABLE vg_user(
  pid INT PRIMARY KEY AUTO_INCREMENT,
  nickName VARCHAR(4),
  sex VARCHAR(1),
  contact VARCHAR(11),
  qq VARCHAR(14),
  platform VARCHAR(64),
  timing VARCHAR(32),
  liveTelecastRoom VARCHAR(64),
  videoUrl VARCHAR(128),
  selfIntroduction VARCHAR(128) 
);

-- vg_academy_text
CREATE TABLE vg_academy_text(
  pid INT PRIMARY KEY AUTO_INCREMENT,
  items VARCHAR(1024),
  seq_academy TINYINT,
  seq_mode TINYINT,
  seq_advance TINYINT,
  seq_equipment TINYINT
);

-- vg_academy_img
CREATE TABLE vg_academy_img(
  pid INT PRIMARY KEY AUTO_INCREMENT,
  pic VARCHAR(128),
  textp VARCHAR(64),
  seq_academy TINYINT,
  seq_mode TINYINT,
  seq_advance TINYINT,
  seq_equipment TINYINT
);

-- vg_features_item
CREATE TABLE vg_features_item(
  pid INT PRIMARY KEY AUTO_INCREMENT,
  pic VARCHAR(128),
  texth2 VARCHAR(64),
  texth4 VARCHAR(64),
  textp VARCHAR(64),
  seq_recommended TINYINT
);

-- vg_news_items
CREATE TABLE vg_news_items(
  pid INT PRIMARY KEY AUTO_INCREMENT,
  href VARCHAR(128),
  pic VARCHAR(128),
  title VARCHAR(64),
  news_body VARCHAR(64),
  news_date VARCHAR(32),
  seq_recommended TINYINT,
  seq_newest TINYINT,
  seq_new_arrival TINYINT,
  seq_edition TINYINT
);


