INSERT INTO vg_features_item VALUES
(NULL, 'home/features_pic_1.png' ,'E.V.I.L引擎' ,'打造绝佳画质','支持120帧激烈的团战。精致绝美的地图、巨龙和野怪，让您在这个栩栩如生的世界沉醉。' , 1),
(NULL, 'home/features_pic_2.png' ,'专业级的' ,'MOBA体验','真正的战争迷雾，真实的对线、游走抓人和团战。操控强大的原创英雄，探索灵活的出装路线。' , 2),
(NULL, 'home/features_pic_3.png' ,'为触屏而生' ,'的超凡手感','同时配备摇杆和点触双操控模式，提供团战中大杀四方所需的跟手性和自主性。' , 3);
INSERT INTO vg_news_items VALUES
(NULL, '/news/1000102' ,'news/prod_2018-09-20_1.jpg' ,'神秘宝箱中奖率说明' , '关于游戏内神秘宝箱中奖率说明' ,'发布日期：2018-09-20 14:32:23' , 0, 1, 1, 0),
(NULL, '/news/1000012' ,'news/prod_2018-09-21_1.jpg' ,'虚荣5V5玩法的未来方向' , '当我们在3.7版本中做出了一系列关于玩法的调整后，现在我们想要花些时间，来谈谈5V5模式未来的发展方向。' ,'发布日期：2018-09-21 10:00:00' , 0, 2, 2, 0),
(NULL, '/news/1000011' ,'news/prod_2018-10-15_1.jpg' ,'3.8版本：英雄&装备平衡性调整' , '感谢大家多等待了两周时间，让我们能更专注的对游戏质量进行优化！其实，除了已经公布的内容外，还有一些改进优化正在进行中，它们将在接下来的几个月内与大家见面，游戏体验也将更上一层楼。不过现在，我们还是想先和大家分享下本期的更新内容。 ' ,'发布日期：2018-10-15 10:19:00' , 0, 3, 0, 1),
(NULL, '/news/1000010' ,'news/prod_2018-10-15_2.jpg' ,'3.8版本：‘森林守护者’伊娜' , '感谢大家多等待了两周时间，让我们能更专注的对游戏质量进行优化！其实，除了已经公布的内容外，还有一些改进优化正在进行中，它们将在接下来的几个月内与大家见面，游戏体验也将更上一层楼。不过现在，我们还是想先和大家分享下本期的更新内容。' ,'发布日期：2018-10-15 10:44:00' , 0, 4, 0, 2),
(NULL, '/news/1000014' ,'news/prod_2018-11-12_1.jpg' ,'虚荣CVPL第二赛段第四轮明日19点再燃战火' , '《虚荣》CVPL（虚荣中国区超级联赛）第二赛段第四轮的比赛将于11月13日（本周二）19点继续上演。想要获得决赛期间的免赛晋级资格，接下来的每一场战斗都不容有失。让我们一起期待《虚荣》战队新一轮的精彩表演！' ,'发布日期：2018-11-22 10:00:00' , 0, 5, 3, 0),
(NULL, '/news/1000016' ,'news/prod_2018-11-22_1.jpg' ,'“双倍荣耀”助阵虚荣CVPL联赛 “黑五”来袭抢限皮' , '这个周末，虚荣CVPL联赛、“双倍荣耀”、“黑五”盛宴，活动三连发！与《虚荣》一起狂欢吧！' ,'发布日期：2018-11-22 16:00:00' , 0, 6, 4, 0),
(NULL, '/news/1000017' ,'news/prod_2018-12-26_1.jpg' ,'虚荣CVPL本周迎来天王山战役 看直播送天赋币' , '《虚荣》CVPL（虚荣中国区超级联赛）第二赛段第六轮的比赛将在本周六（12月1日）16点上演强强对决。同时，在这个周末，记得在游戏内参加看直播送天赋币活动！为你喜爱的虚荣战队加油，限时领史诗天赋币奖励' ,'发布日期：2018-11-29 11:00:00' , 0, 7, 5, 0),
(NULL, '/news/1000018' ,'news/prod_2018-12-05_1.jpg' ,'喜迎《虚荣》3周年，限时送皮肤' , '2015年11月，《虚荣》国服正式上线，转眼间三年已过。为了感谢各位玩家的陪伴和支持，《虚荣》官方特地为国服玩家准备了3周年礼品一份！' ,'发布日期：2018-12-05 11:00:00' , 0, 8, 6, 0),
(NULL, '/news/1000019' ,'news/prod_2018-12-26_1.jpg' ,'虚荣CVPL第七周预告，一大波天赋币继续送出！' , '《虚荣》CVPL”（即虚荣中国区超级联赛）第二赛段第七轮的比赛将于12月8日这周六下午四点准时打响。比赛期间，【游戏内看直播送天赋币活动】仍将继续，欢迎届时守候！' ,'发布日期：2018-12-06 15:00:00' , 0, 9, 7, 0),
(NULL, '/news/1000020' ,'news/prod_2018-12-10_1.jpg' ,'再战海希安 《虚荣》3周年主播招募' , '为庆《虚荣》三周年，12月10日至12月25日虚荣官方联动触手直播再次展开主播招募活动！丰厚的现金奖励和直播间首推机会等你来抢！' ,'发布日期：2018-12-10 10:00:00' , 0, 10, 8, 0),
(NULL, '/news/1000021' ,'news/prod_2018-12-11_1.jpg' ,'3.9版本更新：‘奥术贤王’马格纳斯' , '此次更新将为大家带来一位全新的英雄——“奥术贤王”马格纳斯！这是位法力高强的奥术大师，能从远程对敌人造成群体伤害！除此以外我们还制作了六款全新的皮肤和一些可收集的新物品。' ,'发布日期：2018-12-11 15:00:00' , 0, 11, 0, 3),
(NULL, '/news/1000022' ,'news/prod_2018-12-12_1.jpg' ,'《虚荣》3周年 虚荣锦鲤上线了！' , '继人人可得的三周年皮肤礼品公布后，在这个热闹的庆生季，《虚荣》官方还为众多幸运粉丝追加了各式各样的大礼包！从今天起至12月20日，参与指定《虚荣》官方微博话题转发活动即可抽取“虚荣锦鲤”并有机会解锁全部英雄！' ,'发布日期：2018-12-12 15:00:00' , 0, 12, 9, 0),
(NULL, '/news/1000023' ,'news/prod_2018-12-14_1.jpg' ,'3.9版本：英雄&装备平衡性调整' , '这篇文章里，我们将为大家着重介绍3.9新版本内英雄和装备方面的设定调整' ,'发布日期：2018-12-14 15:00:00' , 0, 13, 0, 4),
(NULL, '/news/1000024' ,'news/prod_2018-12-14_2.jpg' ,'3.9版本更新：冬季赛风暴试炼' , '3.9版本将是一个充满阳光的世界！在回归的冬季赛中，我们为大家准备了全新的风暴试炼&赛季阳光系统。奖励将变得更丰富，游戏也会更加有趣！' ,'发布日期：2018-12-14 17:00:00' , 0, 14, 0, 5),
(NULL, '/news/1000025' ,'news/prod_2018-12-19_1.jpg' ,'虚荣CVPL本周六决赛 观看直播抢皮肤' , '《虚荣》CVPL第二赛段的比赛现终于进入倒计时阶段！这周六12月22日下午三点，决赛将正式打响！观看直播就有机会获取皮肤，还有神秘女嘉宾进行赛事解说！' ,'发布日期：2018-12-19 16:56:43' , 0, 15, 10, 0),
(NULL, '/news/1000026' ,'news/prod_2018-12-26_1.jpg' ,'恭喜Hunters夺得 2018虚荣CVPL总冠军' , '北京时间2018年12月22日，“《虚荣》CVPL”（即虚荣中国区超级联赛）总决赛于当日完成精彩对决，最终Hunters战队夺得了本次赛事总冠军！' ,'发布日期：2018-12-26 15:00:00' , 0, 16, 11, 0),
(NULL, '/news/1000027' ,'news/prod_2018-12-29_1.jpg' ,'快乐不在于有多少人追捧——职业战队选手眼中的《虚荣》' , '时间回转到四年前的秋天，手机业界巨头苹果公司在展示自家产品的同时，《虚荣》这款获得苹果全力支持开发，作为演示新iPhone的强大机能的游戏，进入了玩家们的视野。' ,'发布日期：2018-12-29 17:37:31' , 0, 17, 12, 0),
(NULL, '/news/1000028' ,'news/prod_2019-01-22_1.jpg' ,'《虚荣》触手直播活动开启：每日观看30分钟即可领取ICE！' , '触手活动开启及活动规则介绍' ,'发布日期：2019-01-22 11:00:00' , 0, 18, 13, 0),
(NULL, '/news/1000029' ,'news/prod_2019-01-23_1.jpg' ,'3.10版本英雄&装备平衡性调整' , '感谢大家的等待，让我们能更专注的对游戏质量进行优化！除了已经公布的内容外，还有一些改进优化正在进行中，游戏体验也将更上一层楼。不过现在，我们还是想先和大家分享下本期的更新内容。 ' ,'发布日期：2019-01-23 13:45:06' , 0, 19, 0, 6),
(NULL, '/news/1000030' ,'news/prod_2019-01-23_2.jpg' ,'3.10版本：荒野猎手  伊娃' , '在新版本在，又有一位全新英雄：荒野猎手 伊娃加入战场来了。快来看看吧，另外还有其他的改进优化。' ,'发布日期：2019-01-23 13:47:10' , 0, 20, 0, 7),
(NULL, '/news/1000031' ,'news/prod_2019-01-31_1.jpg' ,'《虚荣》新春展望：这个锐意革新的MOBA正在悄然变化' , '新年行大运，《虚荣》伴你行！感谢诸多玩家对于《虚荣》这款游戏的鼎力支持，在过去的一年中，《虚荣》在游戏玩法以及电子竞技方面都取得不错的进展，这要感谢《虚荣》玩家们一路坚定的支持！' ,'发布日期：2019-01-31 16:21:36' , 0, 21, 14, 0),
(NULL, '/news/1000032' ,'news/prod_2019-02-03_1.jpg' ,'《虚荣》新春直播有礼 报名开播拿奖励' , '主播们注意啦！金猪报喜，欢乐庆新春，长假期间也别忘了开启《虚荣》直播哟！在整个春节期间（2月4日-2月11日），开播海希安的勇士们均可参与猪年签到活动，累计播出4天即可领取ICE福利，还有人气现金奖励！赶快报名参加！' ,'发布日期：2019-02-03 11:00:00' , 0, 22, 15, 0),
(NULL, '/news/1000033' ,'news/prod_2019-02-11_1.jpg' ,'虚荣4.0内的游戏模式' , '现在虚荣将迎来4.0版，我们将借此机会来决定将哪几款模式列为常规模式，哪几款模式列为活动模式。这么做的目的有两个。第一，让新玩家能清晰地知道虚荣这款游戏的核心是什么。第二，通过提供不同的常规游戏模式，让不同兴趣，不同玩法的玩家都有中意的模式可玩。' ,'发布日期：2019-02-11 09:50:04' , 0, 23, 16, 0),
(NULL, '/news/1000034' ,'news/prod_2019-02-11_2.jpg' ,'4.0版本英雄&装备平衡性调整' , '4.0主要加入了新的英雄，以及新的游戏模式，更新用户界面，修复BUG，调整游戏平衡。' ,'发布日期：2019-02-11 09:51:32' , 0, 24, 0, 8),
(NULL, '/news/1000035' ,'news/prod_2019-02-11_3.jpg' ,'4.0更新：太极宗师三风' , '4.0新的英雄加入游戏。以及新的皮肤，新的可收集物件，游戏模式，UI升级，BUG修复，平衡性调整' ,'发布日期：2019-02-11 10:02:06' , 0, 25, 0, 9),
(NULL, '/events/crossplatform' ,'news/prod_2019-02-14_1.jpg' ,'《虚荣》 PC平台账号升级公告' ,'全球首个PC、移动跨平台MOBA游戏《虚荣Vainglory》的PC版将于5月21日进入Beta测试。为保障账户安全和提供更好的服务，我们将在《虚荣》PC版率先推出全新的账号安全绑定功能。' ,'发布日期：2019-02-14 10:48:41' , 2, 26, 17, 0),
(NULL, '/news/1000036' ,'news/prod_2019-02-15_1.jpg' ,'喜迎《虚荣》中文ID上线 限时免费改名' , '《虚荣Vainglory》正式迈入4.0时代，《虚荣》跨平台版本PC Alpha测试现已在国服开启！于此同时，备受中国玩家期待的新功能正式来临——游戏内现已正式支持中文ID！' ,'发布日期：2019-02-15 15:26:26' , 0, 27, 18, 0),
(NULL, '/news/1000037' ,'news/prod_2019-02-20_1.jpg' ,'WESG全球总决赛即将开战' , 'WESG 2018-2019赛季全球总决赛即将在重庆九龙坡开战，近日虚荣项目的分组也已正式公布。中国战队Hunters位于A组，将面对来自日本、韩国和美国战队的挑战。' ,'发布日期：2019-02-20 10:00:00' , 0, 28, 19, 0),
(NULL, '/news/1000038' ,'news/prod_2019-03-06_1.jpg' ,'风暴试炼正式退出虚荣' , '目前的风暴试炼没有达到大家期望。说实话，它同样也没达到我们的期望。由于玩家在游戏中可以在很多地方获得不同类型的货币奖励和物品奖励，因此大家很自然地就没怎么把风暴试炼这个“大宝藏”当回事。' ,'发布日期：2019-03-06 11:50:48' , 0, 29, 20, 0),
(NULL, '/news/1000039' ,'news/prod_2019-03-07_1.jpg' ,'不断尝试新内容的拓荒者们—《虚荣》主播采访实录' , '如果把时间调转回十多年前，没人能说得清楚“主播”是个什么职业。再往前几年，新华字典里都找不出这个词。现如今，这个群体成为了互联网时代的注脚，可以在前面加上形容词加以区分：音乐主播，游戏主播等等，甚至可以具体归结到某个游戏。' ,'发布日期：2019-03-07 14:32:46' , 0, 30, 21, 0),
(NULL, '/news/1000040' ,'news/prod_2019-03-12_1.jpg' ,'WESG 2018-2019全球总决赛，虚荣项目本周六将迎来决赛！' , '上周3月9日、3月10日两天，令人期待的WESG虚荣项目小组赛和半决赛在重庆九龙坡雅诗特酒店圆满结束。经过周末两天的激烈对战，小组赛中：美国队Tribe Gaming、韩国队Team ACE、德国队SLY、新加坡队Team Impunity成功晋级，半决赛中：德国队SLY获得季军！' ,'发布日期：2019-03-12 12:00:00' , 0, 31, 22, 0),
(NULL, '/news/1000041' ,'news/prod_2019-03-13_1.jpg' ,'4.1版本英雄&装备平衡性调整' , '虚荣4.1版本中，我们对大量英雄和装备进行了调整。我们将一如既往的专注对游戏质量和平衡性进行优化！' ,'发布日期：2019-03-13 11:30:24' , 0, 32, 0, 10),
(NULL, '/news/1000042' ,'news/prod_2018-12-14_1.jpg' ,'4.1版本-春季赛开始啦' , '4.1版本上线了新的皮肤：“阴阳宗师”三风，“动能机甲”丝凯伊等。还有一些其他的改动，快来看看吧！' ,'发布日期：2019-03-13 11:52:15' , 0, 33, 0, 11),
(NULL, '/news/1000043' ,'news/prod_2019-03-19_1.jpg' ,'WESG 虚荣全球总决赛 冠军战队Tribe Gaming赛后采访 ' , '上周六3月16日中午，WESG2018-2019全球赛虚荣项目总决赛，Tribe Gaming以大优势打败ACE，拿下本次赛事虚荣项目总冠军！恭喜他们！' ,'发布日期：2019-03-19 12:00:00' , 0, 34, 23, 0),
(NULL, '/news/1000044' ,'news/prod_2019-03-22_1.jpg' ,'此生无悔入虚荣，来生还在海希安——虚荣Vainglory，一个硬核的MOBA手游' , '我，一个菜鸟，在海希安活了975天，有些话想说。' ,'发布日期：2019-03-22 09:00:00' , 0, 35, 24, 0),
(NULL, '/news/1000045' ,'news/prod_2019-03-29_1.jpg' ,'《虚荣Vainglory》华为3月29日首发上线' , '《虚荣Vainglory》华为已于 3月29日 11: 00 正式上线，如果你是华为手机用户，即可前往华为应用市场下载游戏，不仅可以拥有不一样的MOBA体验，更有多重华为专属礼包等你拿。' ,'发布日期：2019-03-29 11:10:00' , 0, 36, 25, 0),
